import React from "react";
import { Link } from "react-scroll";

const Hero = () => {
  return (
    <section
      className="relative h-screen bg-gradient-to-br from-black via-gray-900 to-black flex flex-col justify-center items-center text-center"
    >
      {/* Video Background (if applicable) */}
      <div className="absolute top-0 left-0 w-full h-full bg-black bg-opacity-50"></div>

      {/* Hero Content */}
      <div className="relative z-10 px-6 md:px-12">
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-white mb-4">
          Welcome to <span className="text-cyan-400">QuickRent</span>
        </h1>
        <p className="text-sm sm:text-xl md:text-2xl text-gray-300 mb-8">
          Future-ready gadget rentals at your fingertips.
        </p>
        <Link
          to="gadgets"
          smooth={true}
          duration={500}
          className="px-6 py-3 bg-cyan-400 text-black text-lg font-semibold rounded-full shadow-lg hover:shadow-cyan-400/50 hover:bg-cyan-500 transition cursor-pointer"
        >
          Explore Rentals
        </Link>
      </div>
    </section>
  );
};

export default Hero;
