import React from "react";
import { Link } from "react-scroll";
import { gadgets } from "../assets/assets"; // Import gadgets data

const Hero = () => {
  return (
    <section className="h-screen bg-gradient-to-br from-black via-gray-900 to-black relative overflow-hidden">
      {/* Hero Content */}
      <div className="flex flex-col justify-center items-center text-center h-full z-10 relative">
        <h1 className="text-4xl sm:text-5xl lg:text-6xl font-bold text-white mb-4">
          Welcome to <span className="text-cyan-400">QuickRent</span>
        </h1>
        <p className="text-gray-400 text-lg sm:text-xl lg:text-2xl mb-8">
          Future-ready gadget rentals at your fingertips.
        </p>
        <Link
          to="gadgets"
          smooth={true}
          duration={500}
          className="px-6 py-3 bg-cyan-400 text-black text-sm sm:text-lg font-semibold rounded-full shadow-lg hover:shadow-cyan-400/50 hover:bg-cyan-500 transition cursor-pointer animate-pulse"
        >
          Explore Rentals
        </Link>
      </div>

      {/* Gadget Widgets */}
      {gadgets.slice(0, 8).map((gadget, index) => (
        <div
          key={gadget.id}
          className="absolute bg-gradient-to-br from-gray-900 via-black to-gray-800 p-3 sm:p-4 rounded-lg shadow-lg flex flex-col items-center justify-center text-center text-white transition transform hover:scale-110 z-0"
          style={{
            // Dynamic responsive positioning
            top: `${10 + (index % 4) * 20}vh`, // Adjust for vertical responsiveness
            left: `${5 + (index % 4) * 20}vw`, // Adjust for horizontal responsiveness
            width: "15vw", // Responsive width
            height: "15vw", // Responsive height
            maxWidth: "100px", // Cap size for mobile
            maxHeight: "100px", // Cap size for mobile
            animation: `float ${5 + index}s ease-in-out infinite`,
          }}
        >
          <img
            src={gadget.image}
            alt={gadget.name}
            className="w-3/4 h-3/4 object-contain mb-2"
          />
          <p className="text-xs sm:text-sm font-semibold">{gadget.name}</p>
        </div>
      ))}

      {/* Floating Animation */}
      <style>
        {`
          @keyframes float {
            0%, 100% {
              transform: translateY(0);
            }
            50% {
              transform: translateY(-10px);
            }
          }
        `}
      </style>
    </section>
  );
};

export default Hero;
