import React, { useState } from "react";

const Rentals = () => {
  // Sample gadgets with categories
  const gadgets = [
    { id: 1, name: "Laptop", category: "product mobiles", price: "$20/day", features: "8GB RAM, 512GB SSD", image: "/assets/laptop.png" },
    { id: 2, name: "Camera", category: "category Cameras", price: "$15/day", features: "DSLR, 4K Video", image: "/assets/camera.png" },
    { id: 3, name: "GoPro", category: "product 360 Cameras", price: "$25/day", features: "4K Action Camera", image: "/assets/Gopro.png" },
    { id: 4, name: "Lighting", category: "category Lighting", price: "$30/day", features: "Studio Lighting", image: "/assets/lights.png" },
    { id: 5, name: "Playstation 5", category: "product playstations", price: "$50/day", features: "PS5 Console", image: "/assets/Ps5.png" },
    { id: 6, name: "Drone", category: "category Drones", price: "$40/day", features: "DJI Phantom 4", image: "/assets/drone.png" },
    { id: 7, name: "Microphone", category: "category Audio", price: "$15/day", features: "Studio Quality Mic", image: "/assets/Mic.png" },
    // More gadgets here...
  ];

  // State for selected category
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Categories list
  const categories = [
    "all",
    "category Cameras",
    "category Lenses",
    "category Audio",
    "category Lighting",
    "category Drones",
    "product Gimbals",
    "product 360 Cameras",
    "product ARRI Cameras",
    "product RED Cameras",
    "product mobiles",
    "product playstations",
    "product iots",
  ];

  // Filter gadgets based on selected category
  const filteredGadgets = selectedCategory === "all" 
    ? gadgets 
    : gadgets.filter((gadget) => gadget.category === selectedCategory);

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-black text-white p-8">
      <h1 className="text-4xl font-bold text-cyan-400 text-center mb-8">
        Available Rentals
      </h1>

      {/* Category Filter */}
      <div className="mb-8 text-center">
        <label className="text-lg text-gray-400 mb-2">Filter by Category:</label>
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          className="bg-gray-800 text-white px-4 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-cyan-400"
        >
          {categories.map((category) => (
            <option key={category} value={category}>
              {category.replace("category ", "").replace("product ", "").toUpperCase()}
            </option>
          ))}
        </select>
      </div>

      {/* Gadgets Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 px-4 md:px-20">
        {filteredGadgets.map((gadget) => (
          <div
            key={gadget.id}
            className="bg-gradient-to-br from-gray-900 via-black to-gray-800 p-6 rounded-lg shadow-lg hover:shadow-cyan-400/50 text-center text-white transition"
          >
            <img
              src={gadget.image}
              alt={gadget.name}
              className="w-full h-48 object-contain rounded-lg mb-4"
            />
            <h3 className="text-xl font-bold mb-2">{gadget.name}</h3>
            <p className="text-gray-400 mb-2">{gadget.features}</p>
            <p className="text-cyan-400 font-bold mb-4">{gadget.price}</p>
            <button className="px-4 py-2 bg-cyan-400 text-black rounded-md hover:bg-cyan-500 transition">
              Rent Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Rentals;
