import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link as ScrollLink } from "react-scroll"; // For in-page scrolling

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    navigate("/");
  };

  const handleNavigateToHome = (sectionId) => {
    navigate("/"); // Navigate to the Home Page
    setTimeout(() => {
      // Scroll to the desired section after navigating
      const section = document.getElementById(sectionId);
      if (section) {
        section.scrollIntoView({ behavior: "smooth" });
      }
    }, 100); // Delay to ensure the page loads
  };

  return (
    <nav className="backdrop-blur-lg bg-opacity-20 bg-black fixed top-0 w-full p-4 flex justify-between items-center shadow-lg z-50">
      {/* Logo */}
      <h1
        className="text-xl sm:text-2xl md:text-3xl font-bold text-cyan-400 cursor-pointer"
        onClick={() => navigate("/")} // Always navigate to Home when clicking the logo
      >
        QuickRent
      </h1>

      {/* Desktop Navigation */}
      <ul className="hidden md:flex space-x-6 text-white text-sm sm:text-base md:text-lg lg:text-xl">
        <li>
          <button
            className="hover:text-cyan-400 transition"
            onClick={() => handleNavigateToHome("features")}
          >
            Features
          </button>
        </li>
        <li>
          <button
            className="hover:text-cyan-400 transition"
            onClick={() => handleNavigateToHome("gadgets")}
          >
            Gadgets
          </button>
        </li>
        <li>
          <button
            className="hover:text-cyan-400 transition"
            onClick={() => handleNavigateToHome("contact")}
          >
            Contact
          </button>
        </li>
      </ul>

      {/* Mobile Hamburger Menu */}
      <div className="md:hidden">
        <button
          className="text-cyan-400 text-2xl"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          â˜°
        </button>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <ul className="absolute top-16 left-0 w-full bg-black text-white flex flex-col items-center space-y-4 py-4 shadow-lg text-base sm:text-lg">
          <li>
            <button
              className="hover:text-cyan-400 transition"
              onClick={() => {
                handleNavigateToHome("features");
                setIsMobileMenuOpen(false);
              }}
            >
              Features
            </button>
          </li>
          <li>
            <button
              className="hover:text-cyan-400 transition"
              onClick={() => {
                handleNavigateToHome("gadgets");
                setIsMobileMenuOpen(false);
              }}
            >
              Gadgets
            </button>
          </li>
          <li>
            <button
              className="hover:text-cyan-400 transition"
              onClick={() => {
                handleNavigateToHome("contact");
                setIsMobileMenuOpen(false);
              }}
            >
              Contact
            </button>
          </li>
          <li>
            <button
              onClick={() => {
                handleLogout();
                setIsMobileMenuOpen(false);
              }}
              className="bg-cyan-400 text-black px-6 py-2 rounded-md hover:bg-cyan-500 transition"
            >
              Logout
            </button>
          </li>
        </ul>
      )}

      {/* Desktop Logout Button */}
      <button
        onClick={handleLogout}
        className="hidden md:block bg-cyan-400 text-black px-6 py-2 rounded-md hover:bg-cyan-500 transition text-sm sm:text-base md:text-lg"
      >
        Logout
      </button>
    </nav>
  );
};

export default Navbar;
