import React from "react";
import { FaLaptop, FaTruck, FaWallet } from "react-icons/fa";

const Features = () => {
  const features = [
    { icon: <FaLaptop size={40} className="text-blue-500" />, title: "Wide Variety", desc: "Choose from laptops, cameras, and gaming consoles." },
    { icon: <FaWallet size={40} className="text-blue-500" />, title: "Affordable Pricing", desc: "Budget-friendly rental options." },
    { icon: <FaTruck size={40} className="text-blue-500" />, title: "Quick Delivery", desc: "Fast and reliable delivery service." },
  ];

  return (
    <section id="features" className="py-20 bg-gray-100">
      <h2 className="text-center text-4xl font-bold mb-10">Why Choose QuickRent?</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 px-4 md:px-20">
        {features.map((feature, index) => (
          <div key={index} className="bg-white p-6 rounded shadow text-center">
            {feature.icon}
            <h3 className="text-xl font-bold mt-4">{feature.title}</h3>
            <p className="text-gray-600 mt-2">{feature.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Features;
