import React from "react";
import { Link } from "react-scroll";
import { useNavigate } from "react-router-dom";

const Hero = ({ isDashboard }) => {
  const navigate = useNavigate();

  return (
    <section
      className="relatih-screen bg-gradient-to-br from-black via-gray-900 to-black flex flex-col justify-center items-center text-center"
    >
      <h1 className="text-6xl font-bold text-white mb-4">
        Welcome to <span className="text-cyan-400">QuickRent</span>
      </h1>
      <p className="text-gray-400 text-xl mb-8">
        Future-ready gadget rentals at your fingertips.
      </p>
      {isDashboard ? (
        <Link
          to="gadgets"
          smooth={true}
          duration={500}
          className="px-6 py-3 bg-cyan-400 text-black text-lg font-semibold rounded-full shadow-lg hover:shadow-cyan-400/50 hover:bg-cyan-500 transition cursor-pointer"
        >
          Explore Rentals
        </Link>
      ) : (
        <button
          onClick={() => navigate("/rentals")}
          className="px-6 py-3 bg-cyan-400 text-black text-lg font-semibold rounded-full shadow-lg hover:shadow-cyan-400/50 hover:bg-cyan-500 transition"
        >
          Explore Rentals
        </button>
      )}
    </section>
  );
};

export default Hero;
